        #region ##### CALLBACK METHODS #####

        public Task Filter()
        {
            return OnSubmit.InvokeAsync(adventureFilterDTO);
        }

        #endregion

        #region ##### INTERACTION METHODS #####

        private void TagsKeyPress(KeyboardEventArgs e)
        {
            if (e.Key == "Enter")
            {
                Filter();
            }
        }

        private void Clear()
        {
            adventureFilterDTO.GenreID = 0;
        }

